import React, { useState } from 'react';

function PaymentPage({ cart, totalPrice, onPaymentSuccess }) {
  const [paymentMethod, setPaymentMethod] = useState('');
  const [accountNumber, setAccountNumber] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userId = localStorage.getItem('userId');

    const paymentData = {
      userId,
      paymentMethod,
      accountNumber,
    };

    const response = await fetch('http://localhost:4000/payment/process', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(paymentData),
    });

    const data = await response.json();

    if (data.message) {
      alert('Pembayaran berhasil!');
      onPaymentSuccess();  // This function will be called after a successful payment to reset cart and navigate
    } else {
      alert('Pembayaran gagal: ' + data.error);
    }
  };

  return (
    <div>
      <h2>Proses Pembayaran</h2>
      {cart.length === 0 ? (
        <p>Keranjang Anda kosong, tidak ada produk untuk dibayar.</p>
      ) : (
        <>
          <h3>Total Pembayaran: Rp {totalPrice.toLocaleString()}</h3>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Metode Pembayaran:</label>
              <input
                type="text"
                value={paymentMethod}
                onChange={(e) => setPaymentMethod(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Nomor Rekening:</label>
              <input
                type="text"
                value={accountNumber}
                onChange={(e) => setAccountNumber(e.target.value)}
                required
              />
            </div>
            <button type="submit">Bayar</button>
          </form>
        </>
      )}
    </div>
  );
}

export default PaymentPage;
