import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate, Link, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Login from './components/Login';
import { login } from './api';
import styled from 'styled-components';

// Styled components
const Navbar = styled.nav`
  background-color: #3f51b5;
  color: white;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-right: 20px;
`;

const Button = styled.button`
  background-color: #f44336;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #e53935;
  }
  margin-left: 20px;
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
`;

const NavLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-size: 16px;

  &:hover {
    text-decoration: underline;
  }
`;

const Container = styled.div`
  padding: 20px;
  min-height: 100vh; /* Ensure full screen height */
  display: flex;
  flex-direction: column;
`;

const Footer = styled.footer`
  background-color: #3f51b5;
  color: white;
  text-align: center;
  padding: 10px;
  margin-top: auto; /* Push the footer to the bottom */
`;

function App() {
  const [role, setRole] = useState(localStorage.getItem('role') || null);
  const [cart, setCart] = useState([]);  // Cart state here
  const navigate = useNavigate();

  useEffect(() => {
    if (role) {
      navigate('/home');
    }
  }, [role, navigate]);

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        alert('Login Failed!');
      } else {
        setRole(response.role);
        localStorage.setItem('role', response.role);
        navigate('/home');
      }
    } catch (error) {
      alert('Error during login!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('role');
    setRole(null);
    setCart([]); // Clear cart when logging out
    navigate('/login');
  };

  return (
    <Container>
      <Navbar>
        <Title>E-Toko Beras</Title>
        <NavLinks>
          {role && role === 'pembeli' && (
            <>
              <NavLink to="/home">Home</NavLink>
              <NavLink to="/keranjang">Keranjang</NavLink>
              <NavLink to="/pesanan">Pesanan</NavLink>
            </>
          )}
          {role ? (
            <>
              <span className="text-white me-3">Welcome, {role}</span>
              <Button onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <NavLink to="/login">Login</NavLink>
          )}
        </NavLinks>
      </Navbar>

      <Routes>
        <Route
          path="/home"
          element={
            role === 'pembeli' ? (
              <div>
                <h1 className="text-center my-4">Welcome, Pembeli!</h1>
                <h2 className="text-center mb-4">Daftar Produk</h2>
                <ProductList onAddToCart={(product) => setCart([...cart, product])} />
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        
        <Route
          path="/keranjang"
          element={
            role === 'pembeli' ? (
              <Cart cart={cart} onRemoveFromCart={(product) => setCart(cart.filter(item => item.id !== product.id))} />
            ) : (
              <Navigate to="/cart" />
            )
          }
        />

        <Route
          path="/pesanan"
          element={
            role === 'pembeli' ? (
              <h1 className="text-center">Pesanan Anda</h1>
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route path="/login" element={<Login onLogin={handleLogin} />} />
      </Routes>

      <Footer>
        <p>&copy; 2024 E-Toko Beras. Semua Hak Dilindungi.</p>
      </Footer>
    </Container>
  );
}

export default App;
