import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { getPesanan, updatePesananStatus } from '../api'; // API Helper functions

// Styled Components
const Container = styled.div`
  padding: 20px;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const Th = styled.th`
  border: 1px solid #ddd;
  padding: 8px;
  background-color: #f2f2f2;
  text-align: left;
`;

const Td = styled.td`
  border: 1px solid #ddd;
  padding: 8px;
`;

const Button = styled.button`
  background-color: #007bff; /* Biru untuk tombol konfirmasi */
  color: white;
  padding: 10px;
  margin: 5px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3; /* Biru gelap saat hover */
  }

  &:disabled {
    background-color: #d3d3d3; /* Abu-abu untuk tombol disabled */
    cursor: not-allowed;
  }
`;

const StatusBadge = styled.span`
  background-color: ${(props) =>
    props.status === 'Barang Dikirim' ? 'green' : 'orange'};
  color: white;
  padding: 5px 10px;
  border-radius: 5px;
  font-size: 12px;
  font-weight: bold;
`;

const PaginationContainer = styled.div`
  margin-top: 20px;
  display: flex;
  justify-content: flex-end; /* Geser ke kanan */
  align-items: center;
  gap: 10px;
`;

const PaginationButton = styled.button`
  background-color: #007bff; /* Warna biru */
  color: white;
  padding: 10px;
  margin: 5px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3; /* Warna biru gelap saat hover */
  }

  &:disabled {
    background-color: #d3d3d3; /* Warna abu-abu untuk tombol disabled */
    cursor: not-allowed;
  }
`;

const DataPesanan = () => {
  const [pesananList, setPesananList] = useState([]); // Menyimpan data pesanan dari database
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5; // Jumlah item per halaman

  // Fetch data pesanan dari database saat komponen di-mount
  useEffect(() => {
    fetchPesanan();
  }, []);

  const fetchPesanan = async () => {
    const data = await getPesanan(); // Ambil data dari API
    setPesananList(data); // Set data ke state pesananList
  };

  const handleKonfirmasi = async (id) => {
    if (window.confirm('Apakah Anda yakin ingin mengonfirmasi pesanan ini?')) {
      await updatePesananStatus(id, 'Barang Dikirim'); // Update status ke "Barang Dikirim"
      fetchPesanan(); // Refresh data pesanan
    }
  };

  const totalPages = Math.ceil(pesananList.length / itemsPerPage);

  const handlePreviousPage = () => {
    setCurrentPage((prev) => Math.max(prev - 1, 1));
  };

  const handleNextPage = () => {
    setCurrentPage((prev) => Math.min(prev + 1, totalPages));
  };

  const paginatedData = pesananList.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <Container>
      <h2>Data Pesanan</h2>
      {/* Tabel Data Pesanan */}
      <Table>
        <thead>
          <tr>
            <Th>ID</Th>
            <Th>Nama Produk</Th>
            <Th>Jumlah</Th>
            <Th>Status</Th>
            <Th>Metode Pembayaran</Th>
            <Th>Nomor Rekening</Th>
            <Th>Aksi</Th>
          </tr>
        </thead>
        <tbody>
          {paginatedData.map((pesanan) => (
            <tr key={pesanan.id}>
              <Td>{pesanan.id}</Td>
              <Td>{pesanan.produk_nama}</Td>
              <Td>{pesanan.jumlah}</Td>
              <Td>
                <StatusBadge status={pesanan.status}>{pesanan.status}</StatusBadge>
              </Td>
              <Td>{pesanan.metode_pembayaran}</Td>
              <Td>{pesanan.nomor_rekening}</Td>
              <Td>
                <Button
                  onClick={() => handleKonfirmasi(pesanan.id)}
                  disabled={pesanan.status === 'Barang Dikirim'}
                >
                  Konfirmasi
                </Button>
              </Td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Pagination */}
      <PaginationContainer>
        <PaginationButton onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous
        </PaginationButton>
        <span>
          Halaman {currentPage} dari {totalPages}
        </span>
        <PaginationButton onClick={handleNextPage} disabled={currentPage === totalPages}>
          Next
        </PaginationButton>
      </PaginationContainer>
    </Container>
  );
};

export default DataPesanan;
