import React, { useState } from 'react';

function Cart({ cart, onRemoveFromCart }) {
  const [selectedIds, setSelectedIds] = useState([]);

  // Handle checkbox change
  const handleCheckboxChange = (id, isChecked) => {
    if (isChecked) {
      setSelectedIds([...selectedIds, id]); // Add cart.id to selectedIds
    } else {
      setSelectedIds(selectedIds.filter((selectedId) => selectedId !== id)); // Remove cart.id from selectedIds
    }
  };

  // Calculate the total price of selected items
  const totalPrice = cart.reduce((total, item) => {
    if (selectedIds.includes(item.id)) {
      return total + item.harga * item.jumlah;
    }
    return total;
  }, 0);

  // Handle checkout process
  const handleCheckout = () => {
    if (selectedIds.length === 0) {
      alert('Silakan pilih produk yang ingin dibeli.');
    } else {
      alert('Pembayaran berhasil diproses.');
      // Implement further logic to process payment
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                <input
                  type="checkbox"
                  onChange={(e) => handleCheckboxChange(item.id, e.target.checked)} // Use cart.id here
                  checked={selectedIds.includes(item.id)} // Check if cart.id is in selectedIds
                />
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
                <button onClick={() => onRemoveFromCart(item.id)}>Hapus</button> {/* Pass cart.id */}
              </li>
            ))}
          </ul>
          <div>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </div>
        </>
      )}
      <button onClick={handleCheckout} disabled={cart.length === 0 || selectedIds.length === 0}>
        {cart.length === 0 || selectedIds.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
