import React from 'react';
import styled from 'styled-components'; // Import styled-components

// Styled components
const CartContainer = styled.div`
  padding: 20px;
`;

const CartTitle = styled.h2`
  text-align: center;
  margin-bottom: 20px;
`;

const CartList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const CartItem = styled.li`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #f4f4f4;
  padding: 10px;
  margin-bottom: 10px;
  border-radius: 5px;
`;

const Button = styled.button`
  background-color: #f44336;
  color: white;
  padding: 5px 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #e53935;
  }
`;

const TotalPrice = styled.div`
  text-align: center;
  font-size: 20px;
  margin-top: 20px;
  font-weight: bold;
`;

const CheckoutButton = styled.button`
  background-color: #4CAF50;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  width: 100%;
  
  &:hover {
    background-color: #45a049;
  }

  &:disabled {
    background-color: #e0e0e0;
    cursor: not-allowed;
  }
`;

function Cart({ cart, onRemoveFromCart, onCheckout }) {
  const totalPrice = cart.reduce((total, item) => total + item.harga * item.jumlah, 0);

  return (
    <CartContainer>
      <CartTitle>Keranjang Belanja</CartTitle>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <CartList>
            {cart.map((item) => (
              <CartItem key={item.id}>
                <div>
                  {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
                </div>
                <Button onClick={() => onRemoveFromCart(item)}>Hapus</Button>
              </CartItem>
            ))}
          </CartList>
          <TotalPrice>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </TotalPrice>
        </>
      )}
      <CheckoutButton onClick={onCheckout} disabled={cart.length === 0}>
        {cart.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </CheckoutButton>
    </CartContainer>
  );
}

export default Cart;
