import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { processPayment } from '../api'; // Import API function

function PaymentPage({ cart }) {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedItems, setSelectedItems] = useState([]);
  const [metodePembayaran, setMetodePembayaran] = useState('');
  const [nomorRekening, setNomorRekening] = useState('');

  useEffect(() => {
    // Get selected cart IDs passed from Cart.js
    const selectedIds = location.state?.selectedIds || [];
    const filteredItems = cart.filter((item) => selectedIds.includes(item.id));
    setSelectedItems(filteredItems);
  }, [location.state, cart]);

  const handlePayment = async () => {
    if (!metodePembayaran || !nomorRekening) {
      alert('Silakan lengkapi data pembayaran.');
      return;
    }

    const userId = localStorage.getItem('userId');
    // Prepare the selected items data
    const selectedData = selectedItems.map((item) => ({
      id: item.id, // Cart ID
      id_produk: item.id_produk,
      jumlah: item.jumlah,
    }));

    // Call the API to process payment
    const response = await processPayment(userId, selectedData, metodePembayaran, nomorRekening);

    if (response.message) {
      alert('Pembayaran berhasil diproses.');
      navigate('/pesanan'); // Navigate to orders page
    } else {
      alert('Gagal memproses pembayaran.');
    }
  };

  return (
    <div>
      <h2>Halaman Pembayaran</h2>
      <form>
        <div>
          <label>Metode Pembayaran:</label>
          <input
            type="text"
            value={metodePembayaran}
            onChange={(e) => setMetodePembayaran(e.target.value)}
            placeholder="Masukkan metode pembayaran"
          />
        </div>
        <div>
          <label>Nomor Rekening:</label>
          <input
            type="text"
            value={nomorRekening}
            onChange={(e) => setNomorRekening(e.target.value)}
            placeholder="Masukkan nomor rekening"
          />
        </div>
        <h3>Produk yang Dibeli:</h3>
        <ul>
          {selectedItems.map((item) => (
            <li key={item.id}>
              {item.nama} - Rp {item.harga.toLocaleString()} x {item.jumlah} kg
            </li>
          ))}
        </ul>
        <button type="button" onClick={handlePayment}>
          Pesan
        </button>
      </form>
    </div>
  );
}

export default PaymentPage;
