import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Login from './components/Login';
import { login } from './api';
import styled from 'styled-components'; // Import styled-components

// Styled components
const Navbar = styled.nav`
  background-color: #3f51b5;
  color: white;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-right: 20px; /* Menambahkan jarak antara title dan tombol */
`;

const Button = styled.button`
  background-color: #f44336;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #e53935;
  }
  margin-left: 20px; /* Menambahkan jarak antara tombol dan teks welcome */
`;

const Container = styled.div`
  padding: 20px;
`;

const Footer = styled.footer`
  background-color: #3f51b5;
  color: white;
  text-align: center;
  padding: 10px;
  margin-top: auto;
`;

const ProductListContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  gap: 20px;
  margin: 20px 0;
`;

const ProductCard = styled.div`
  background-color: white;
  width: 30%;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  text-align: center;
  transition: transform 0.3s ease;
  box-sizing: border-box;
`;

const ProductImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 8px;
  margin-bottom: 10px;
`;

const ProductName = styled.h3`
  font-size: 20px;
  margin: 10px 0;
`;

const ProductDetails = styled.p`
  font-size: 16px;
`;

const ProductButton = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 12px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const CartSection = styled.div`
  text-align: center;
  margin-top: 40px;
`;

const CartButton = styled.button`
  background-color: #4caf50;
  padding: 12px 20px;
  color: white;
  border-radius: 5px;
  border: none;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

// Main App component
function App() {
  const [role, setRole] = useState(localStorage.getItem('role') || null);
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (role) {
      navigate('/home');
    }
  }, [role, navigate]);

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        alert('Login Failed!');
      } else {
        setRole(response.role);
        localStorage.setItem('role', response.role); // Persist login state
        navigate('/home');
      }
    } catch (error) {
      alert('Error during login!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('role');
    setRole(null);
    navigate('/login');
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCart(cart.filter(item => item.id !== product.id));
  };

  return (
    <Container>
      <Navbar className="navbar navbar-expand-lg navbar-light bg-primary">
        <Title>E-Toko Beras</Title>
        <div>
          {role ? (
            <>
              <span className="text-white me-3">Welcome, {role}</span>
              <Button onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <a href="/login" className="text-white">Login</a>
          )}
        </div>
      </Navbar>

      {role === null ? (
        <Login onLogin={handleLogin} />
      ) : role === 'pembeli' ? (
        <Routes>
          <Route path="/home" element={
            <div>
              <h1 className="text-center my-4">Welcome, Pembeli!</h1>
              <h2 className="text-center mb-4">Daftar Produk</h2>
              <ProductListContainer>
                <ProductList onAddToCart={handleAddToCart} />
              </ProductListContainer>
              <CartSection>
                <h3>Keranjang Belanja</h3>
                {cart.length > 0 ? (
                  <div>
                    {cart.map((item, index) => (
                      <div key={index}>
                        <p>{item.nama} - {item.harga} per kg</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p>Keranjang belanja Anda kosong</p>
                )}
                <CartButton onClick={() => alert('Checkout')}>Proses Pembayaran</CartButton>
              </CartSection>
            </div>
          } />
        </Routes>
      ) : (
        <Routes>
          <Route path="/home" element={<h1 className="text-center">Welcome, Penjual</h1>} />
        </Routes>
      )}

      <Footer>
        <p>&copy; 2024 E-Toko Beras. Semua Hak Dilindungi.</p>
      </Footer>
    </Container>
  );
}

export default App;
