// src/App.js
import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate, Link, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Login from './components/Login';
import PaymentPage from './components/PaymentPage';
import PesananPage from './components/PesananPage'; // Import PesananPage
import { login, getProduk, getCart, addToCart, removeFromCart } from './api'; // Import API methods
import styled from 'styled-components';

const Navbar = styled.nav`
  background-color: #3f51b5;
  color: white;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%; /* Pastikan lebar navbar penuh */
  box-sizing: border-box; /* Agar padding tidak menambah ukuran elemen */
`;

const Title = styled.h1`
  font-size: 24px;
  margin-right: 20px;
`;

const Button = styled.button`
  background-color: #f44336;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #e53935;
  }
  margin-left: 20px;
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
`;

const NavLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-size: 16px;

  &:hover {
    background-color: #3f5142;
  }
`;

const Container = styled.div`
  padding: 20px;
  min-height: 100vh;
  width: 100%; /* Memastikan menggunakan lebar penuh layar */
  max-width: 1200px; /* Batas maksimal lebar konten, opsional */
  margin: 0 auto; /* Agar konten selalu di tengah layar */
  display: flex;
  flex-direction: column;
`;

const Footer = styled.footer`
  background-color: #3f51b5;
  color: white;
  text-align: center;
  padding: 10px;
  margin-top: auto;
  width: 100%; /* Full width */
  box-sizing: border-box;
`;

function App() {
  const [role, setRole] = useState(localStorage.getItem('role') || null);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (role) {
      fetchCart();
      fetchProducts();
    }
  }, [role]);

  const fetchCart = async () => {
    const userId = localStorage.getItem('userId');
    const cartData = await getCart(userId);
    setCart(cartData);
  };

  const fetchProducts = async () => {
    const productData = await getProduk();
    setProducts(productData);
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        alert('Login Gagal!');
      } else {
        setRole(response.role);
        localStorage.setItem('role', response.role);
        localStorage.setItem('userId', response.id);
        navigate('/home');
      }
    } catch (error) {
      alert('kesalahan saat login!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('role');
    localStorage.removeItem('userId');
    setRole(null);
    setCart([]);
    navigate('/login');
  };

  const handleAddToCart = async (product) => {
    const userId = localStorage.getItem('userId');
    const response = await addToCart(userId, product.id, 1); // Adding 1 quantity
    if (response.message) {
      fetchCart();
      alert('Produk Ditambahkan Ke Keranjang!');
    }
  };

  const handleRemoveFromCart = async (id) => {
    const userId = localStorage.getItem('userId'); // Ambil userId dari localStorage
    try {
      const response = await removeFromCart(userId, id); // Panggil API untuk menghapus item berdasarkan ID
      if (response.message) {
        fetchCart(); // Ambil ulang keranjang setelah penghapusan
        alert('Produk berhasil dihapus dari keranjang!');
      } else {
        alert('Gagal menghapus produk dari keranjang: ' + response.error);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Terjadi kesalahan saat menghapus produk.');
    }
  };
  


  const handlePayment = (metodePembayaran, nomorRekening) => {
    // Process payment, typically this would update the order status in your database
    alert(`Pembayaran berhasil dengan metode ${metodePembayaran}. Nomor rekening: ${nomorRekening}`);
    // You may update the payment status in your orders table here.
    navigate('/home');
  };

  return (
    <Container>
      <Navbar>
        <Title>E-Toko Beras</Title>
        <NavLinks>
          {role && role === 'pembeli' && (
            <>
              <NavLink to="/home">Home</NavLink>
              <NavLink to="/keranjang">Keranjang</NavLink>
              <NavLink to="/pesanan">Pesanan</NavLink>
            </>
          )}
          {role ? (
            <>
              <span className="text-white me-3">Welcome, {role}</span>
              <Button onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <NavLink to="/login">Login</NavLink>
          )}
        </NavLinks>
      </Navbar>

      <Routes>
        <Route
          path="/home"
          element={
            role === 'pembeli' ? (
              <div>
                <h1 className="text-center my-4">Welcome, Pembeli!</h1>
                <h2 className="text-center mb-4">Daftar Produk</h2>
                <ProductList products={products} onAddToCart={handleAddToCart} />
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/keranjang"
          element={
            role === 'pembeli' ? (
              <Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/pesanan"
          element={
            role === 'pembeli' ? (
              <PesananPage />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/payment"
          element={
            role === 'pembeli' ? (
              <PaymentPage cart={cart} onPayment={handlePayment} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
      </Routes>

      <Footer>
        <p>&copy; 2024 E-Toko Beras. Semua Hak Dilindungi.</p>
      </Footer>
    </Container>
  );
}

export default App;
