import React from 'react';

function Cart({ cart, onRemoveFromCart, onCheckout }) {
  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              {item.nama} - {item.harga} per kg x {item.jumlah} kg
              <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
            </li>
          ))}
        </ul>
      )}
      <button onClick={onCheckout}>Proses Pembayaran</button>
    </div>
  );
}

export default Cart;
