import React from 'react';

function ProductItem({ product, onAddToCart }) {
  return (
    <div>
      <h3>{product.nama}</h3>
      <p>Harga: Rp {product.harga} per kg</p>
      <button onClick={() => onAddToCart(product)}>Tambah ke Keranjang</button>
    </div>
  );
}

export default ProductItem;
