import React, { useState } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';
import { login } from './api';

function App() {
  const [role, setRole] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // useNavigate should work now

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        setErrorMessage('Login gagal!');
      } else {
        setRole(response.role);
        navigate('/home'); // navigate to /home after successful login
      }
    } catch (error) {
      setErrorMessage('Terjadi kesalahan saat login!');
    }
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCart(cart.filter(item => item.id !== product.id));
  };

  const handleCheckout = () => {
    alert('Pembayaran selesai');
    // Handle checkout logic here
  };

  return (
    <div className="App">
      {errorMessage && <div className="error-popup">{errorMessage}</div>}
      {!role ? (
        <Login onLogin={handleLogin} />
      ) : role === 'pembeli' ? (
        <Routes>
          <Route path="/home" element={
            <>
              <h1>Welcome, Pembeli</h1>
              <ProductList onAddToCart={handleAddToCart} />
              <Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />
            </>
          } />
        </Routes>
      ) : (
        <Routes>
          <Route path="/home" element={
            <>
              <h1>Welcome, Penjual</h1>
              <OrderList />
            </>
          } />
        </Routes>
      )}
    </div>
  );
}

export default App;
