import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';

function App() {
  const [role, setRole] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (username, password) => {
    try {
      const loginResponse = await login(username, password); // Menggunakan fungsi login dari api.js
      if (loginResponse.error) {
        setErrorMessage('Login gagal!');
      } else {
        setRole(loginResponse.role); // Set role based on response from API
      }
    } catch (error) {
      setErrorMessage('Terjadi kesalahan saat login');
    }
  };

  return (
    <Router>
      <div className="App">
        {errorMessage && <div className="error-popup">{errorMessage}</div>}
        {!role ? (
          <Login onLogin={handleLogin} />
        ) : (
          <Switch>
            <Route path="/home">
              {role === 'pembeli' ? (
                <>
                  <h1>Welcome, Pembeli</h1>
                  <ProductList />
                  <Cart />
                </>
              ) : (
                <>
                  <h1>Welcome, Penjual</h1>
                  <OrderList />
                </>
              )}
            </Route>
          </Switch>
        )}
      </div>
    </Router>
  );
}

export default App;
