import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';
import Login from './components/Login';

function App() {
  const [role, setRole] = useState(null);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = (username, password) => {
    if (username === 'pembeli' && password === 'password') {
      setRole('pembeli');
      navigate('/home');  // Redirect to home
    } else if (username === 'penjual' && password === 'password') {
      setRole('penjual');
      navigate('/orders');  // Redirect to orders for penjual
    } else {
      setErrorMessage('Login gagal!');
    }
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCart(cart.filter(item => item.id !== product.id));
  };

  const handleCheckout = () => {
    alert('Pembayaran selesai');
    // Handle checkout logic here
  };

  return (
    <Router>
      <div className="app-container">
        {/* Sidebar */}
        <div className="sidebar">
          <h2 style={{ color: 'white', textAlign: 'center' }}>E-TokoBeras</h2>
          <Link to="/home">Home</Link>
          <Link to="/cart">Keranjang</Link>
          <Link to="/orders">Pesanan</Link>
          <Link to="/" onClick={() => setRole(null)}>Logout</Link>
        </div>

        {/* Content Area */}
        <div className="content">
          {errorMessage && <div className="error-popup">{errorMessage}</div>}
          {!role ? (
            <Login onLogin={handleLogin} />
          ) : role === 'pembeli' ? (
            <Routes>
              <Route path="/home" element={
                <>
                  <h1>Welcome, Pembeli</h1>
                  <ProductList onAddToCart={handleAddToCart} />
                  <Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />
                </>
              } />
              <Route path="/cart" element={<Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />} />
            </Routes>
          ) : (
            <Routes>
              <Route path="/orders" element={<OrderList />} />
            </Routes>
          )}
        </div>
      </div>
    </Router>
  );
}

export default App;
