import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Cart({ cart }) {
  const [selectedItems, setSelectedItems] = useState([]);
  const navigate = useNavigate();

  const handleCheckboxChange = (item, isChecked) => {
    if (isChecked) {
      setSelectedItems((prevSelectedItems) => [...prevSelectedItems, item]);
    } else {
      setSelectedItems((prevSelectedItems) =>
        prevSelectedItems.filter((selectedItem) => selectedItem.id !== item.id)
      );
    }
  };

  const handleCheckout = () => {
    if (selectedItems.length === 0) {
      alert('Silakan pilih produk yang ingin dibeli.');
    } else {
      navigate('/payment', { state: { items: selectedItems } });
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <input
                type="checkbox"
                onChange={(e) => handleCheckboxChange(item, e.target.checked)}
                checked={selectedItems.some((selectedItem) => selectedItem.id === item.id)}
              />
              {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
            </li>
          ))}
        </ul>
      )}
      <button onClick={handleCheckout} disabled={selectedItems.length === 0}>
        Proses Pembayaran
      </button>
    </div>
  );
}

export default Cart;
