import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate, Link, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Login from './components/Login';
import Payment from './components/Payment';  // Import the Payment component
import { login, getProduk, getCart, addToCart, removeFromCart, pesanProduk } from './api'; 
import styled from 'styled-components';

// Styled components (Navbar, Button, etc. stay the same)

function App() {
  const [role, setRole] = useState(localStorage.getItem('role') || null);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (role) {
      fetchCart();
      fetchProducts();
    }
  }, [role]);

  const fetchCart = async () => {
    const userId = localStorage.getItem('userId');
    const cartData = await getCart(userId);
    setCart(cartData);
  };

  const fetchProducts = async () => {
    const productData = await getProduk();
    setProducts(productData);
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        alert('Login Failed!');
      } else {
        setRole(response.role);
        localStorage.setItem('role', response.role);
        localStorage.setItem('userId', response.id);
        navigate('/home');
      }
    } catch (error) {
      alert('Error during login!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('role');
    localStorage.removeItem('userId');
    setRole(null);
    setCart([]);
    navigate('/login');
  };

  const handleAddToCart = async (product) => {
    const userId = localStorage.getItem('userId');
    const response = await addToCart(userId, product.id, 1);
    if (response.message) {
      fetchCart();
      alert('Product added to cart!');
    }
  };

  const handleRemoveFromCart = async (product) => {
    const userId = localStorage.getItem('userId');
    const response = await removeFromCart(userId, product.id);
    if (response.message) {
      fetchCart();
      alert('Product removed from cart!');
    }
  };

  const handlePaymentConfirmation = async () => {
    const userId = localStorage.getItem('userId');
    const totalPrice = cart.reduce((total, item) => total + item.harga * item.jumlah, 0);

    // Call the backend to process the order
    for (let item of cart) {
      await pesanProduk(userId, item.id, item.jumlah);
    }

    alert('Pembayaran berhasil diproses!');
    setCart([]); // Clear cart after successful payment
    navigate('/home');
  };

  return (
    <Container>
      <Navbar>
        <Title>E-Toko Beras</Title>
        <NavLinks>
          {role && role === 'pembeli' && (
            <>
              <NavLink to="/home">Home</NavLink>
              <NavLink to="/keranjang">Keranjang</NavLink>
              <NavLink to="/pesanan">Pesanan</NavLink>
            </>
          )}
          {role ? (
            <>
              <span className="text-white me-3">Welcome, {role}</span>
              <Button onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <NavLink to="/login">Login</NavLink>
          )}
        </NavLinks>
      </Navbar>

      <Routes>
        <Route
          path="/home"
          element={
            role === 'pembeli' ? (
              <div>
                <h1 className="text-center my-4">Welcome, Pembeli!</h1>
                <h2 className="text-center mb-4">Daftar Produk</h2>
                <ProductList
                  products={products}
                  onAddToCart={handleAddToCart}
                />
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route
          path="/keranjang"
          element={
            role === 'pembeli' ? (
              <Cart
                cart={cart}
                onRemoveFromCart={handleRemoveFromCart}
                onCheckout={() => navigate('/payment')}
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route
          path="/payment"
          element={
            role === 'pembeli' ? (
              <Payment
                cart={cart}
                totalPrice={cart.reduce((total, item) => total + item.harga * item.jumlah, 0)}
                onConfirmPayment={handlePaymentConfirmation}
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route path="/pesanan" element={<h1 className="text-center">Pesanan Anda</h1>} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
      </Routes>

      <Footer>
        <p>&copy; 2024 E-Toko Beras. Semua Hak Dilindungi.</p>
      </Footer>
    </Container>
  );
}

export default App;
