import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';
import { login } from './api';  // Import API for login

function App() {
  const [role, setRole] = useState(null);
  const [cart, setCart] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();  // Navigate hook for redirection

  // Retrieve role from localStorage when the app loads
  useEffect(() => {
    const storedRole = localStorage.getItem('role');
    if (storedRole) {
      setRole(storedRole);
    }
  }, []);

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        setErrorMessage('Login failed!');
      } else {
        setRole(response.role);
        localStorage.setItem('role', response.role);  // Save role in localStorage
        navigate('/home');  // Redirect to home after successful login
      }
    } catch (error) {
      setErrorMessage('An error occurred during login!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('role');  // Remove role from localStorage
    setRole(null);  // Reset role in the state
    navigate('/');  // Redirect to login page
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCart(cart.filter(item => item.id !== product.id));
  };

  const handleCheckout = () => {
    alert('Payment completed');
    // Handle checkout logic here
  };

  return (
    <div className="App">
      {errorMessage && <div className="error-popup">{errorMessage}</div>}
      {!role ? (
        <Login onLogin={handleLogin} />
      ) : role === 'pembeli' ? (
        <Routes>
          <Route path="/home" element={
            <>
              <h1>Welcome, Pembeli</h1>
              <ProductList onAddToCart={handleAddToCart} />
              <Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />
              <button onClick={handleLogout}>Logout</button>
            </>
          } />
        </Routes>
      ) : (
        <Routes>
          <Route path="/home" element={
            <>
              <h1>Welcome, Penjual</h1>
              <OrderList />
              <button onClick={handleLogout}>Logout</button>
            </>
          } />
        </Routes>
      )}
    </div>
  );
}

export default App;
