import React, { useState, useEffect } from 'react';
import { getPesanan } from './api'; // Import the API function to fetch orders

function PesananPage() {
  const [orders, setOrders] = useState([]);
  const userId = localStorage.getItem('userId');

  // Fetch orders when the component mounts
  useEffect(() => {
    const fetchOrders = async () => {
      const orderData = await getPesanan(userId);
      setOrders(orderData);
    };

    fetchOrders();
  }, [userId]);

  return (
    <div>
      <h2>Daftar Pesanan Anda</h2>
      {orders.length === 0 ? (
        <p>Belum ada pesanan.</p>
      ) : (
        <div>
          {orders.map((order) => (
            <div key={order.id} style={{ marginBottom: '20px', borderBottom: '1px solid #ccc' }}>
              <img
                src={`http://localhost:4000/images/${order.produk_gambar}`} // Assuming images are stored at this path
                alt={order.produk_nama}
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
              />
              <p><strong>Produk:</strong> {order.produk_nama}</p>
              <p><strong>Jumlah:</strong> {order.jumlah} kg</p>
              <p><strong>Status:</strong> {order.status}</p>
              <p><strong>Metode Pembayaran:</strong> {order.metode_pembayaran}</p>
              <p><strong>Nomor Rekening:</strong> {order.nomor_rekening}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default PesananPage;
