import React, { useState } from 'react';

function Cart({ cart, onRemoveFromCart }) {
  const [selectedIds, setSelectedIds] = useState([]); // Store selected item ids

  // Handle checkbox change (select or unselect items)
  const handleCheckboxChange = (id, isChecked) => {
    if (isChecked) {
      // Add id to selectedIds if checked
      setSelectedIds((prevSelectedIds) => [...prevSelectedIds, id]);
    } else {
      // Remove id from selectedIds if unchecked
      setSelectedIds((prevSelectedIds) => prevSelectedIds.filter((selectedId) => selectedId !== id));
    }
  };

  // Calculate the total price of selected items
  const totalPrice = cart.reduce((total, item) => {
    if (selectedIds.includes(id)) {
      return total + item.harga * item.jumlah; // Multiply price by quantity
    }
    return total;
  }, 0);

  // Handle the checkout process
  const handleCheckout = () => {
    if (selectedIds.length === 0) {
      alert("Silakan pilih produk yang ingin dibeli.");
    } else {
      alert("Pembayaran berhasil diproses.");
      // Perform payment logic here (e.g., send data to backend)
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={id}>
                <input
                  type="checkbox"
                  onChange={(e) => handleCheckboxChange(id, e.target.checked)} // Use id for selection
                  checked={selectedIds.includes(id)} // Check if id is in selectedIds
                />
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
                <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
              </li>
            ))}
          </ul>
          <div>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </div>
        </>
      )}

      <button onClick={handleCheckout} disabled={cart.length === 0 || selectedIds.length === 0}>
        {cart.length === 0 || selectedIds.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
