import React, { useEffect, useState } from 'react';
import { getCart } from '../api'; // Ensure this is correctly imported

function Cart({ cart, onRemoveFromCart, onCheckout }) {
  const [cartItems, setCartItems] = useState([]);

  // Fetch cart items
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      getCart(userId).then((data) => setCartItems(data));
    }
  }, []);

  const totalPrice = cartItems.reduce((total, item) => total + item.harga * item.quantity, 0);

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cartItems.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <ul>
            {cartItems.map((item) => (
              <li key={item.id}>
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.quantity} kg
                <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
              </li>
            ))}
          </ul>
          <div>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </div>
        </>
      )}
      <button onClick={onCheckout} disabled={cartItems.length === 0}>
        {cartItems.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
