import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { createOrder } from '../api'; // Import createOrder API

function PaymentPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedItems, setSelectedItems] = useState([]);
  const [metodePembayaran, setMetodePembayaran] = useState('');
  const [nomorRekening, setNomorRekening] = useState('');

  useEffect(() => {
    // Get selected items passed from Cart.js
    const items = location.state?.items || [];
    setSelectedItems(items);
  }, [location.state]);

  const handlePayment = async () => {
    if (!metodePembayaran || !nomorRekening) {
      alert('Silakan lengkapi data pembayaran.');
      return;
    }

    const userId = localStorage.getItem('userId');
    const response = await createOrder(userId, selectedItems, metodePembayaran, nomorRekening);

    if (response.message) {
      alert('Pesanan berhasil diproses.');
      navigate('/pesanan'); // Redirect to orders page
    } else {
      alert('Gagal memproses pesanan.');
    }
  };

  return (
    <div>
      <h2>Halaman Pembayaran</h2>
      <form>
        <div>
          <label>Metode Pembayaran:</label>
          <input
            type="text"
            value={metodePembayaran}
            onChange={(e) => setMetodePembayaran(e.target.value)}
            placeholder="Masukkan metode pembayaran"
          />
        </div>
        <div>
          <label>Nomor Rekening:</label>
          <input
            type="text"
            value={nomorRekening}
            onChange={(e) => setNomorRekening(e.target.value)}
            placeholder="Masukkan nomor rekening"
          />
        </div>
        <h3>Produk yang Dibeli:</h3>
        <ul>
          {selectedItems.map((item) => (
            <li key={item.id}>
              {item.nama} - Rp {item.harga.toLocaleString()} x {item.jumlah} kg
            </li>
          ))}
        </ul>
        <button type="button" onClick={handlePayment}>
          Pesan
        </button>
      </form>
    </div>
  );
}

export default PaymentPage;
