import React, { useState, useEffect } from 'react';
import { getPesanan } from '../api'; // Correct the path to api.js
import styled from 'styled-components';

const Container = styled.div`
  padding: 20px;
`;

const PesananPage = () => {
  const [orders, setOrders] = useState([]);
  const userId = localStorage.getItem('userId'); // Assuming userId is stored in localStorage

  useEffect(() => {
    if (userId) {
      const fetchOrders = async () => {
        const ordersData = await getPesanan(userId);
        setOrders(ordersData);
      };

      fetchOrders();
    }
  }, [userId]);

  return (
    <Container>
      <h2>Pesanan Anda</h2>
      {orders.length === 0 ? (
        <p>Anda belum memiliki pesanan.</p>
      ) : (
        <ul>
          {orders.map((order) => (
            <li key={order.id}>
              Produk ID: {order.id_produk} - Jumlah: {order.jumlah} - Status: {order.status} - Metode Pembayaran: {order.metode_pembayaran}
            </li>
          ))}
        </ul>
      )}
    </Container>
  );
};

export default PesananPage;
