import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Cart({ cart, onRemoveFromCart }) {
  const [selectedIds, setSelectedIds] = useState([]); // Store selected cart IDs
  const navigate = useNavigate();

  // Handle checkbox change (select or unselect ids)
  const handleCheckboxChange = (id, isChecked) => {
    if (isChecked) {
      setSelectedIds((prevSelectedIds) => [...prevSelectedIds, id]);
    } else {
      setSelectedIds((prevSelectedIds) => prevSelectedIds.filter((selectedId) => selectedId !== id));
    }
  };

  // Handle checkout process
  const handleCheckout = () => {
    if (selectedIds.length === 0) {
      alert('Silakan pilih produk yang ingin dibeli.');
    } else {
      // Navigate to payment page and pass selected cart IDs as state
      navigate('/payment', { state: { selectedIds } });
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <div>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                <input
                  type="checkbox"
                  onChange={(e) => handleCheckboxChange(item.id, e.target.checked)} // Use id for selection
                  checked={selectedIds.includes(item.id)} // Check if id is in selectedIds
                />
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
                {/* Tombol untuk menghapus item */}
                <button onClick={() => onRemoveFromCart(item.id)}>Hapus</button>
              </li>
            ))}
          </ul>
        </div>
      )}
      <button onClick={handleCheckout} disabled={cart.length === 0 || selectedIds.length === 0}>
        {cart.length === 0 || selectedIds.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
