import React, { useState, useEffect } from 'react';
import { getPesanan } from '../api'; // Import API method to fetch orders

const PesananPage = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null); // State for error handling
  const userId = localStorage.getItem('userId'); // Get the userId from localStorage

  useEffect(() => {
    // Fetch the orders for the logged-in user
    const fetchOrders = async () => {
      try {
        const ordersData = await getPesanan(userId); // Call API method
        if (ordersData.error) {
          setError(ordersData.error);
        } else {
          setOrders(ordersData); // Set fetched orders to state
        }
      } catch (err) {
        setError('Gagal memuat pesanan. Silakan coba lagi.'); // Handle fetch errors
        console.error(err);
      }
    };

    if (userId) {
      fetchOrders();
    } else {
      setError('User ID tidak ditemukan. Silakan login ulang.');
    }
  }, [userId]);

  return (
    <div>
      <h2>Pesanan Anda</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error message */}
      {orders.length === 0 && !error ? (
        <p>Anda belum memiliki pesanan.</p>
      ) : (
        <ul>
          {orders.map((order) => (
            <li key={order.id} style={{ marginBottom: '20px', border: '1px solid #ddd', padding: '10px' }}>
              <img
                src={order.produk_gambar}
                alt={order.produk_nama}
                width="100"
                style={{ display: 'block', marginBottom: '10px' }}
              />
              <div><strong>Produk:</strong> {order.produk_nama}</div>
              <div><strong>Jumlah:</strong> {order.jumlah} kg</div>
              <div><strong>Status:</strong> {order.status}</div>
              <div><strong>Metode Pembayaran:</strong> {order.metode_pembayaran}</div>
              <div><strong>Nomor Rekening:</strong> {order.nomor_rekening}</div>
              <div><strong>Status Produk:</strong> {order.status}</div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PesananPage;
