import React, { useState } from 'react';
import { makePayment } from '../api'; // Import the makePayment function

const PaymentPage = ({ cart }) => {
  const [paymentMethod, setPaymentMethod] = useState('');
  const [accountNumber, setAccountNumber] = useState('');
  const userId = localStorage.getItem('userId'); // Get the userId from localStorage

  const handlePayment = async () => {
    const response = await makePayment(userId, paymentMethod, accountNumber);

    if (response.message) {
      alert(response.message); // Display the payment success message
      // Optionally navigate to another page (e.g., orders page)
    } else {
      alert('Pembayaran gagal: ' + response.error); // Show error message
    }
  };

  return (
    <div>
      <h2>Proses Pembayaran</h2>
      <div>
        <label>
          Metode Pembayaran:
          <select
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value="">Pilih Metode Pembayaran</option>
            <option value="transfer">Transfer</option>
            <option value="credit_card">Kartu Kredit</option>
          </select>
        </label>
      </div>

      <div>
        <label>
          Nomor Rekening:
          <input
            type="text"
            value={accountNumber}
            onChange={(e) => setAccountNumber(e.target.value)}
          />
        </label>
      </div>

      <button onClick={handlePayment}>Bayar</button>
    </div>
  );
};

export default PaymentPage;
