const API_URL = 'http://localhost:4000';  // Pastikan backend berjalan di port ini

export const login = async (username, password) => {
  const response = await fetch(`${API_URL}/login`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ username, password }),
  });
  return response.json();
};

export const getProduk = async () => {
  const response = await fetch(`${API_URL}/produk`);
  return response.json();
};

export const pesanProduk = async (idPembeli, idProduk, jumlah) => {
  const response = await fetch(`${API_URL}/pesan`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ id_pembeli: idPembeli, id_produk: idProduk, jumlah }),
  });
  return response.json();
};
