import React from 'react';

function Cart({ cart, onRemoveFromCart }) {
  const totalPrice = cart.reduce((total, item) => {
    const itemPrice = item.harga || 0;
    const itemQuantity = item.jumlah || 0;
    return total + itemPrice * itemQuantity;
  }, 0);

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                {item.nama} - Rp {(item.harga || 0).toLocaleString()} per kg x {(item.jumlah || 0)} kg
                <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
              </li>
            ))}
          </ul>
          <div>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </div>
        </>
      )}
      <button disabled={cart.length === 0}>
        {cart.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
