import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function PaymentPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedItems, setSelectedItems] = useState([]);
  const [metodePembayaran, setMetodePembayaran] = useState('');
  const [nomorRekening, setNomorRekening] = useState('');

  useEffect(() => {
    // Ambil produk yang dipilih dari state yang dikirim dari halaman Cart
    const items = location.state?.items || [];
    console.log('Initial items from location.state:', items); // Debugging awal
    setSelectedItems(items);
  }, [location.state]);

  const processOrder = async () => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      console.error('User ID not found in localStorage');
      alert('Silakan login terlebih dahulu.');
      return;
    }

    if (!metodePembayaran.trim()) {
      alert('Metode pembayaran harus diisi.');
      return;
    }

    if (!nomorRekening.trim() || isNaN(nomorRekening)) {
      alert('Nomor rekening harus berupa angka.');
      return;
    }

    const validItems = selectedItems.map((item) => {
      if (!item.quantity || typeof item.quantity !== 'number' || item.quantity <= 0) {
        console.error('Invalid item quantity:', item);
        alert('Terdapat item dengan jumlah (quantity) tidak valid.');
        return null;
      }
      return {
        id_produk: item.id,
        jumlah: item.quantity,
      };
    }).filter((item) => item !== null);

    if (validItems.length === 0) {
      alert('Tidak ada item valid untuk diproses.');
      return;
    }

    const payload = {
      id_pembeli: userId,
      items: validItems,
      metode_pembayaran: metodePembayaran,
      nomor_rekening: nomorRekening,
    };

    console.log('Payload:', payload);

    try {
      const response = await fetch('http://localhost:4000/pesan', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const result = await response.json();
      if (response.ok) {
        alert('Pesanan berhasil diproses.');
        navigate('/pesanan');
      } else {
        console.error(result.error);
        alert('Gagal memproses pesanan: ' + result.error);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Terjadi kesalahan saat memproses pesanan.');
    }
  };

  return (
    <div>
      <h2>Halaman Pembayaran</h2>
      <form>
        <div>
          <label>Metode Pembayaran:</label>
          <input
            type="text"
            value={metodePembayaran}
            onChange={(e) => setMetodePembayaran(e.target.value)}
            placeholder="Masukkan metode pembayaran"
          />
        </div>
        <div>
          <label>Nomor Rekening:</label>
          <input
            type="text"
            value={nomorRekening}
            onChange={(e) => setNomorRekening(e.target.value)}
            placeholder="Masukkan nomor rekening"
          />
        </div>
        <h3>Produk yang Dibeli:</h3>
        <ul>
          {selectedItems.map((item) => (
            <li key={item.id}>
              {item.nama} - Rp {item.harga.toLocaleString()} x {item.quantity || 0} kg
            </li>
          ))}
        </ul>
        <button type="button" onClick={processOrder}>
          Pesan
        </button>
      </form>
    </div>
  );
}

export default PaymentPage;
