import React, { useState, useEffect } from 'react';
import { getPesanan } from '../api'; // Import API method to fetch orders

const PesananPage = () => {
  const [orders, setOrders] = useState([]);
  const userId = localStorage.getItem('userId'); // Get the userId from localStorage
const getPesanan = async (userId) => {
  const response = await fetch(`http://localhost:4000/pesanan/${userId}`);
  if (!response.ok) {
    throw new Error('Failed to fetch orders');
  }
  const data = await response.json();
  return data;
};


  useEffect(() => {
    // Fetch the orders for the logged-in user
    const fetchOrders = async () => {
      const ordersData = await getPesanan(userId);
      setOrders(ordersData);
    };

    fetchOrders();
  }, [userId]);

  return (
    <div>
      <h2>Pesanan Anda</h2>
      {orders.length === 0 ? (
        <p>Anda belum memiliki pesanan.</p>
      ) : (
        <ul>
          {orders.map((order) => (
            <li key={order.id}>
              <img src={order.produk_gambar} alt={order.produk_nama} width="100" />
              <div>{order.produk_nama}</div>
              <div>{order.jumlah} kg</div>
              <div>Status: {order.status}</div>
              <div>Metode Pembayaran: {order.metode_pembayaran}</div>
              <div>Nomor Rekening: {order.nomor_rekening}</div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PesananPage;
