import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function PaymentPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedItems, setSelectedItems] = useState([]);
  const [metodePembayaran, setMetodePembayaran] = useState('');
  const [nomorRekening, setNomorRekening] = useState('');

  useEffect(() => {
    // Ambil produk yang dipilih dari state yang dikirim dari halaman Cart
    const items = location.state?.items || [];
    setSelectedItems(items);
  }, [location.state]);

  const processOrder = async () => {
    if (!metodePembayaran.trim() || !nomorRekening.trim() || selectedItems.length === 0) {
      alert('Lengkapi semua data untuk melanjutkan!');
      return;
    }
  
    try {
      const payload = {
        id_pembeli: localStorage.getItem('userId'), // Ambil userId langsung dari localStorage
        items: selectedItems.map((item) => ({
          id_produk: item.id, // ID produk
          jumlah: item.jumlah, // Jumlah produk
        })),
        metode_pembayaran: metodePembayaran,
        nomor_rekening: nomorRekening,
      };
  
      console.log('Payload:', payload); // Debugging
  
      const response = await fetch('http://localhost:4000/pesan', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),  // Pastikan format JSON benar
      });
  
      const result = await response.json();
      if (response.ok) {
        alert('Pesanan berhasil diproses.');
        navigate('/pesanan');
      } else {
        console.error(result.error);
        alert('Gagal memproses pesanan: ' + result.error);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Terjadi kesalahan saat memproses pesanan.');
    }
  };  

  return (
    <div>
      <h2>Halaman Pembayaran</h2>
      <form>
        <div>
          <label>Metode Pembayaran:</label>
          <input
            type="text"
            value={metodePembayaran}
            onChange={(e) => setMetodePembayaran(e.target.value)}
            placeholder="Masukkan metode pembayaran"
          />
        </div>
        <div>
          <label>Nomor Rekening:</label>
          <input
            type="text"
            value={nomorRekening}
            onChange={(e) => setNomorRekening(e.target.value)}
            placeholder="Masukkan nomor rekening"
          />
        </div>
        <h3>Produk yang Dibeli:</h3>
        <ul>
          {selectedItems.map((item) => (
            <li key={item.id}>
              {item.nama} - Rp {item.harga.toLocaleString()} x {item.jumlah} kg
            </li>
          ))}
        </ul>
        <button type="button" onClick={processOrder}>
          Pesan
        </button>
      </form>
    </div>
  );
}

export default PaymentPage;
