import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { getPesanan } from '../api'; // Import API

const Container = styled.div`
  padding: 20px;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const Th = styled.th`
  border: 1px solid #ddd;
  padding: 8px;
  background-color: #f2f2f2;
  text-align: left;
`;

const Td = styled.td`
  border: 1px solid #ddd;
  padding: 8px;
`;

const Button = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  margin: 5px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const DataPesanan = () => {
  const [pesanan, setPesanan] = useState([]);

  useEffect(() => {
    const fetchPesanan = async () => {
      try {
        const data = await getPesanan();
        setPesanan(data);
      } catch (error) {
        console.error('Gagal mengambil data pesanan:', error);
      }
    };

    fetchPesanan();
  }, []);

  return (
    <Container>
      <h2>Data Pesanan</h2>
      <Table>
        <thead>
          <tr>
            <Th>ID</Th>
            <Th>Produk</Th>
            <Th>Jumlah</Th>
            <Th>Harga</Th>
            <Th>Total</Th>
            <Th>Status</Th>
            <Th>Metode Pembayaran</Th>
            <Th>Nomor Rekening</Th>
          </tr>
        </thead>
        <tbody>
          {pesanan.map((order) => (
            <tr key={order.id}>
              <Td>{order.id}</Td>
              <Td>{order.produk_nama}</Td>
              <Td>{order.jumlah}</Td>
              <Td>{order.produk_harga}</Td>
              <Td>{order.jumlah * order.produk_harga}</Td>
              <Td>{order.status}</Td>
              <Td>{order.metode_pembayaran}</Td>
              <Td>{order.nomor_rekening}</Td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default DataPesanan;
