import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';
import { login } from './api';  // Import API untuk login

function App() {
  const [role, setRole] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = (username, password) => {
    // For now, use dummy credentials
    if (username === 'pembeli' && password === 'password') {
      setRole('pembeli');
    } else if (username === 'penjual' && password === 'password') {
      setRole('penjual');
    } else {
      setErrorMessage('Login gagal!');
    }
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCart(cart.filter(item => item.id !== product.id));
  };

  const handleCheckout = () => {
    alert('Pembayaran selesai');
    // Handle checkout logic
  };

  return (
    <Router>
      <div className="App">
        {errorMessage && <div className="error-popup">{errorMessage}</div>}
        {!role ? (
          <Login onLogin={handleLogin} />
        ) : (
          <Routes>
            <Route path="/" element={
              <>
                <h1>Welcome, {role === 'pembeli' ? 'Pembeli' : 'Penjual'}</h1>
                {role === 'pembeli' ? (
                  <>
                    <ProductList onAddToCart={handleAddToCart} />
                    <Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />
                  </>
                ) : (
                  <OrderList />
                )}
              </>
            } />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
