import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Login from './components/Login';
import { login } from './api';
import styled from 'styled-components'; // Import styled-components

// Styled components
const Navbar = styled.nav`
  background-color: #3f51b5;
  color: white;
  padding: 10px 20px;
//   display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h1`
  font-size: 24px;
`;

const Button = styled.button`
  background-color: #f44336;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #e53935;
  }
`;

const Container = styled.div`
  padding: 20px;
`;

const Footer = styled.footer`
  background-color: #3f51b5;
  color: white;
  text-align: center;
  padding: 10px;
  margin-top: auto;
`;

// Main App component
function App() {
  const [role, setRole] = useState(localStorage.getItem('role') || null);
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (role) {
      navigate('/home');
    }
  }, [role, navigate]);

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        alert('Login Failed!');
      } else {
        setRole(response.role);
        localStorage.setItem('role', response.role); // Persist login state
        navigate('/home');
      }
    } catch (error) {
      alert('Error during login!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('role');
    setRole(null);
    navigate('/login');
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCart(cart.filter(item => item.id !== product.id));
  };

  return (
    <Container>
      <Navbar className="navbar navbar-expand-lg navbar-light bg-primary">
        <Title>E-Toko Beras</Title>
        <div>
          {role ? (
            <>
              <span className="text-white me-3">Welcome, {role}</span>
              <Button onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <a href="/login" className="text-white">Login</a>
          )}
        </div>
      </Navbar>

      {role === null ? (
        <Login onLogin={handleLogin} />
      ) : role === 'pembeli' ? (
        <Routes>
          <Route path="/home" element={
            <div>
              <h1 className="text-center my-4">Welcome, Pembeli!</h1>
              <h2 className="text-center mb-4">Daftar Produk</h2>
              <ProductList onAddToCart={handleAddToCart} />
              <Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} />
            </div>
          } />
        </Routes>
      ) : (
        <Routes>
          <Route path="/home" element={<h1 className="text-center">Welcome, Penjual</h1>} />
        </Routes>
      )}

      <Footer>
        <p>&copy; 2024 E-Toko Beras. Semua Hak Dilindungi.</p>
      </Footer>
    </Container>
  );
}

export default App;
