import React, { useState } from 'react';  // Only use useState

function Cart({ cart, onRemoveFromCart }) {
  const [selectedItems, setSelectedItems] = useState([]);

  // Handle checkbox change (select or unselect items)
  const handleCheckboxChange = (item, isChecked) => {
    if (isChecked) {
      // Add item to selectedItems if checked
      setSelectedItems((prevSelectedItems) => [...prevSelectedItems, item]);
    } else {
      // Remove item from selectedItems if unchecked
      setSelectedItems((prevSelectedItems) => prevSelectedItems.filter((selectedItem) => selectedItem.id !== item.id));
    }
  };

  // Calculate the total price of selected items
  const totalPrice = selectedItems.reduce((total, item) => {
    // Ensure valid numbers for harga and jumlah
    if (item.harga && item.jumlah) {
      return total + (item.harga * item.jumlah);
    }
    return total;
  }, 0);

  // Handle the checkout process
  const handleCheckout = () => {
    if (selectedItems.length === 0) {
      alert("Silakan pilih produk yang ingin dibeli.");
    } else {
      // You can replace this with real payment processing logic
      alert("Pembayaran berhasil diproses.");
      // Optionally, reset the cart or perform other actions after checkout
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <ul>
            {cart.map((item, index) => (
              <li key={item.id}>
                <input
                  type="checkbox"
                  onChange={(e) => handleCheckboxChange(item, e.target.checked)}
                  checked={selectedItems.some((selectedItem) => selectedItem.id === item.id)} // Check if this item is in selectedItems
                />
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
                <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
              </li>
            ))}
          </ul>
          <div>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </div>
        </>
      )}

      <button onClick={handleCheckout} disabled={cart.length === 0 || selectedItems.length === 0}>
        {cart.length === 0 || selectedItems.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
