import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Cart({ cart, onRemoveFromCart }) {
  const [selectedIds, setSelectedIds] = useState([]); // Untuk menyimpan id item yang dipilih untuk pembayaran
  const navigate = useNavigate();

  // Fungsi untuk menangani perubahan pada checkbox
  const handleCheckboxChange = (id, isChecked) => {
    if (isChecked) {
      setSelectedIds((prevSelectedIds) => [...prevSelectedIds, id]); // Tambahkan id ke selectedIds jika dicentang
    } else {
      setSelectedIds((prevSelectedIds) => prevSelectedIds.filter((selectedId) => selectedId !== id)); // Hapus id dari selectedIds jika tidak dicentang
    }
  };

  // Fungsi untuk menangani checkout
  const handleCheckout = () => {
    if (selectedIds.length === 0) {
      alert('Silakan pilih produk yang ingin dibeli.');
    } else {
      // Arahkan ke halaman pembayaran dan kirim selectedIds sebagai state
      navigate('/payment', { state: { selectedIds } });
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <div>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                {/* Checkbox untuk memilih item untuk pembayaran */}
                <input
                  type="checkbox"
                  onChange={(e) => handleCheckboxChange(item.id, e.target.checked)}
                  checked={selectedIds.includes(item.id)} // Periksa apakah item.id ada di selectedIds
                />
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
                {/* Tombol untuk menghapus item */}
                <button onClick={() => onRemoveFromCart(item.id)}>Hapus</button>
              </li>
            ))}
          </ul>
          {/* Tampilkan total harga dari item yang dipilih */}
          <div>
            <strong>
              Total Harga: Rp{' '}
              {cart
                .filter((item) => selectedIds.includes(item.id)) // Hanya hitung item yang dipilih
                .reduce((total, item) => total + item.harga * item.jumlah, 0)
                .toLocaleString()}
            </strong>
          </div>
        </div>
      )}
      {/* Tombol untuk memproses pembayaran */}
      <button onClick={handleCheckout} disabled={selectedIds.length === 0}>
        {selectedIds.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
