import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';
import { login } from './api';  // Import API untuk login

function App() {
  const [role, setRole] = useState(null);
  const [cart, setCart] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  // Memeriksa status login dari localStorage ketika aplikasi dimuat
  useEffect(() => {
    const storedRole = localStorage.getItem('role');
    if (storedRole) {
      setRole(storedRole);
    }
  }, []);

  // Fungsi untuk menangani login
  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        setErrorMessage('Login gagal!');
      } else {
        setRole(response.role);
        // Menyimpan role pengguna di localStorage
        localStorage.setItem('role', response.role);
      }
    } catch (error) {
      setErrorMessage('Terjadi kesalahan saat login!');
    }
  };

  // Fungsi untuk logout
  const handleLogout = () => {
    // Menghapus status login dari localStorage
    localStorage.removeItem('role');
    setRole(null);
  };

  return (
    <Router> {/* Ensure this Router is only here at the top level */}
      <div className="app-container">
        {/* Sidebar */}
        <div className="sidebar">
          <h3 style={{ color: 'white', paddingLeft: '10px' }}>Menu</h3>
          <Link to="/home">Home</Link>
          <Link to="/cart">Keranjang</Link>
          <Link to="/orders">Pesanan</Link>
          <button onClick={handleLogout}>Logout</button>
        </div>

        {/* Content */}
        <div className="content">
          {role ? (
            <Routes>
              <Route path="/home" element={<ProductList />} />
              <Route path="/cart" element={<Cart cart={cart} />} />
              <Route path="/orders" element={<OrderList />} />
            </Routes>
          ) : (
            <Login onLogin={handleLogin} errorMessage={errorMessage} />
          )}
        </div>
      </div>
    </Router>
  );
}

export default App;
