import React, { useState, useEffect } from 'react';
import { addToCart } from '../api'; // Assuming you have an API function to add products to the cart

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('http://localhost:4000/produk'); // Your backend API URL
      const data = await response.json();
      setProducts(data); // Store the fetched data in state
    };

    fetchProducts(); // Call the fetch function
  }, []); // Empty dependency array ensures this runs only once when the component mounts

  const handleAddToCart = async (product) => {
    const userId = localStorage.getItem('userId');
    const response = await addToCart(userId, product.id, 1); // Add product to cart with quantity 1
    if (response.message) {
      alert(`${product.nama} has been added to the cart!`);
    }
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      fontFamily: '"Roboto", Arial, sans-serif',
    },
    header: {
      fontSize: '2rem',
      color: '#333',
      marginBottom: '20px',
    },
    productList: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'center',
      gap: '20px',
      maxWidth: '1200px',
    },
    productCard: {
      width: '250px',
      border: '1px solid #ddd',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden',
      textAlign: 'center',
      backgroundColor: '#fff',
    },
    productImage: {
      width: '100%',
      height: '150px',
      objectFit: 'cover',
    },
    productInfo: {
      padding: '10px',
    },
    productButton: {
      marginTop: '10px',
      padding: '10px',
      color: '#fff',
      backgroundColor: '#4CAF50',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '1rem',
    },
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Welcome, Pembeli!</h1>
      <h2 style={styles.header}>Daftar Produk</h2>
      <div style={styles.productList}>
        {products.map((product) => (
          <div key={product.id} style={styles.productCard}>
            <img
              src="/images/background.png"
              alt={product.nama}
              style={styles.productImage}
            />
            <div style={styles.productInfo}>
              <h3>{product.nama}</h3>
              <p>Harga: Rp {product.harga.toLocaleString()}</p>
              <p>Stok: {product.stok} kg</p>
              <button
                style={styles.productButton}
                onClick={() => handleAddToCart(product)}
              >
                Tambah ke Keranjang
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
