import React, { useState } from 'react';  // Only use useState

function Cart({ cart, onRemoveFromCart, onCheckout }) {
  // State to track selected items
  const [selectedItems, setSelectedItems] = useState([]);

  // Calculate the total price of selected items
  const totalPrice = selectedItems.reduce((total, item) => total + item.harga * item.jumlah, 0);

  // Handle checkbox change (select or unselect items)
  const handleCheckboxChange = (item, isChecked) => {
    if (isChecked) {
      setSelectedItems([...selectedItems, item]);
    } else {
      setSelectedItems(selectedItems.filter((selectedItem) => selectedItem.id !== item.id));
    }
  };

  // Handle the checkout process
  const handleCheckout = () => {
    if (selectedItems.length === 0) {
      alert("Silakan pilih produk yang ingin dibeli.");
    } else {
      // Call the onCheckout function passed from App.js
      onCheckout(selectedItems);
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart && cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <ul>
            {(cart || []).map((item) => (
              <li key={item.id}>
                <input
                  type="checkbox"
                  onChange={(e) => handleCheckboxChange(item, e.target.checked)}
                  checked={selectedItems.some((selectedItem) => selectedItem.id === item.id)}
                />
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.jumlah} kg
                <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
              </li>
            ))}
          </ul>
          <div>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </div>
        </>
      )}

      <button onClick={handleCheckout} disabled={cart && cart.length === 0}>
        {cart && cart.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>
    </div>
  );
}

export default Cart;
