import React, { useEffect, useState } from 'react';
import { getFilteredPesanan, confirmPesanan } from '../api';
import styled from 'styled-components';

// Styled Components
const Container = styled.div`
  padding: 20px;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const Th = styled.th`
  border: 1px solid #ddd;
  padding: 8px;
  background-color: #f2f2f2;
`;

const Td = styled.td`
  border: 1px solid #ddd;
  padding: 8px;
`;

const Button = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  margin: 5px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const DataPesanan = () => {
  const [pesananList, setPesananList] = useState([]);

  useEffect(() => {
    fetchPesanan();
  }, []);

  const fetchPesanan = async () => {
    try {
      const data = await getFilteredPesanan();
      setPesananList(data);
    } catch (error) {
      console.error('Failed to fetch pesanan:', error);
    }
  };

  const handleConfirm = async (id) => {
    try {
      await confirmPesanan(id);
      alert('Pesanan berhasil dikonfirmasi sebagai "Pesanan Dikirim".');
      fetchPesanan(); // Refresh data setelah konfirmasi
    } catch (error) {
      console.error('Failed to confirm pesanan:', error);
      alert('Gagal mengkonfirmasi pesanan.');
    }
  };

  return (
    <Container>
      <h2>Data Pesanan</h2>
      <Table>
        <thead>
          <tr>
            <Th>ID</Th>
            <Th>Produk</Th>
            <Th>Jumlah</Th>
            <Th>Harga</Th>
            <Th>Status</Th>
            <Th>Metode Pembayaran</Th>
            <Th>Nomor Rekening</Th>
            <Th>Aksi</Th>
          </tr>
        </thead>
        <tbody>
          {pesananList.map((pesanan) => (
            <tr key={pesanan.id}>
              <Td>{pesanan.id}</Td>
              <Td>{pesanan.produk_nama}</Td>
              <Td>{pesanan.jumlah}</Td>
              <Td>{pesanan.produk_harga}</Td>
              <Td>{pesanan.status}</Td>
              <Td>{pesanan.metode_pembayaran}</Td>
              <Td>{pesanan.nomor_rekening}</Td>
              <Td>
                {pesanan.status === 'Pesanan Diproses' && (
                  <Button onClick={() => handleConfirm(pesanan.id)}>Konfirmasi</Button>
                )}
              </Td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default DataPesanan;
