import React from 'react';

function Cart({ cart, onRemoveFromCart }) {
  return (
    <div className="cart-section">
      <h3>Keranjang Belanja</h3>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              {item.name} - Rp {item.price} per kg
              <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Cart;
