import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate, Link, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Login from './components/Login';
import { login, getProduk, getCart, addToCart } from './api'; // Import API methods
import styled from 'styled-components';

// Styled components
const Navbar = styled.nav`
  background-color: #3f51b5;
  color: white;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-right: 20px;
`;

const Button = styled.button`
  background-color: #f44336;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #e53935;
  }
  margin-left: 20px;
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
`;

const NavLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-size: 16px;

  &:hover {
    text-decoration: underline;
  }
`;

const Container = styled.div`
  padding: 20px;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Footer = styled.footer`
  background-color: #3f51b5;
  color: white;
  text-align: center;
  padding: 10px;
  margin-top: auto;
`;

function App() {
  const [role, setRole] = useState(localStorage.getItem('role') || null);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState([]);
  const [notification, setNotification] = useState(''); // State to manage notifications
  const navigate = useNavigate();

  useEffect(() => {
    if (role) {
      fetchCart();
      fetchProducts();
    }
  }, [role]);

  const fetchCart = async () => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      const cartData = await getCart(userId);
      setCart(cartData);
    }
  };

  const fetchProducts = async () => {
    const productData = await getProduk();
    setProducts(productData);
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await login(username, password);
      if (response.error) {
        alert('Login Failed!');
      } else {
        setRole(response.role);
        localStorage.setItem('role', response.role);
        localStorage.setItem('userId', response.id);
        navigate('/home');
      }
    } catch (error) {
      alert('Error during login!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('role');
    localStorage.removeItem('userId');
    setRole(null);
    setCart([]);
    navigate('/login');
  };

  const handleAddToCart = async (product) => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      const response = await addToCart(userId, product.id, 1); // Adding 1 quantity
      if (response.message) {
        setNotification(`${product.nama} has been added to your cart!`); // Display notification
        fetchCart(); // Refresh the cart after adding a product
        setTimeout(() => setNotification(''), 3000); // Hide notification after 3 seconds
      } else {
        alert('Failed to add to cart');
      }
    }
  };

  return (
    <Container>
      <Navbar>
        <Title>E-Toko Beras</Title>
        <NavLinks>
          {role && role === 'pembeli' && (
            <>
              <NavLink to="/home">Home</NavLink>
              <NavLink to="/keranjang">Keranjang</NavLink>
              <NavLink to="/pesanan">Pesanan</NavLink>
            </>
          )}
          {role ? (
            <>
              <span className="text-white me-3">Welcome, {role}</span>
              <Button onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <NavLink to="/login">Login</NavLink>
          )}
        </NavLinks>
      </Navbar>

      {/* Notification for add to cart */}
      {notification && <div style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', marginTop: '10px' }}>{notification}</div>}

      <Routes>
        <Route
          path="/home"
          element={
            role === 'pembeli' ? (
              <div>
                <h1 className="text-center my-4">Welcome, Pembeli!</h1>
                <h2 className="text-center mb-4">Daftar Produk</h2>
                <ProductList
                  products={products}
                  onAddToCart={handleAddToCart}
                />
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route
          path="/keranjang"
          element={
            role === 'pembeli' ? (
              <Cart
                cart={cart}
                onRemoveFromCart={(product) =>
                  setCart(cart.filter((item) => item.id !== product.id))
                }
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route
          path="/pesanan"
          element={
            role === 'pembeli' ? (
              <h1 className="text-center">Pesanan Anda</h1>
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route path="/login" element={<Login onLogin={handleLogin} />} />
      </Routes>

      <Footer>
        <p>&copy; 2024 E-Toko Beras. Semua Hak Dilindungi.</p>
      </Footer>
    </Container>
  );
}

export default App;
