import React, { useState, useEffect } from 'react';
import ProductItem from './ProductItem';

function ProductList({ onAddToCart }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch the products from the API
    fetch('http://localhost:4000/produk')
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to fetch products');
        }
        return res.json();
      })
      .then((data) => {
        setProducts(data);
        setLoading(false); // Finished loading data
      })
      .catch((err) => {
        setError(err.message); // Handle errors during fetch
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading products...</p>; // Show loading state
  }

  if (error) {
    return <p>Error: {error}</p>; // Show error message
  }

  return (
    <div>
      <h2>Daftar Produk</h2>
      <ul style={{ listStyleType: 'none', padding: '0' }}>
        {products.map((product) => (
          <li key={product.id} style={{ marginBottom: '15px' }}>
            <ProductItem product={product} onAddToCart={onAddToCart} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
