import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';

function App() {
  const [role, setRole] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = (username, password) => {
    console.log("Login attempt:", username, password);  // Log the username and password
    if (username === 'pembeli' && password === 'password') {
      setRole('pembeli');  // Set role to pembeli
    } else if (username === 'penjual' && password === 'password') {
      setRole('penjual');  // Set role to penjual
    } else {
      setErrorMessage('Login gagal!');
    }
  };

  return (
    <Router>
      <div className="App">
        {errorMessage && <div className="error-popup">{errorMessage}</div>}
        {!role ? (
          <Login onLogin={handleLogin} />
        ) : role === 'pembeli' ? (
          <Routes>
            <Route path="/home" element={
              <>
                <h1>Welcome, Pembeli</h1>
                <ProductList />
                <Cart />
              </>
            } />
          </Routes>
        ) : (
          <Routes>
            <Route path="/home" element={
              <>
                <h1>Welcome, Penjual</h1>
                <OrderList />
              </>
            } />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
