import React, { useState } from 'react';

// Modal component
const PaymentModal = ({ show, onClose, onPayment }) => {
  const [metodePembayaran, setMetodePembayaran] = useState('');
  const [nomorRekening, setNomorRekening] = useState('');

  const handlePayment = () => {
    if (metodePembayaran && nomorRekening) {
      onPayment(metodePembayaran, nomorRekening); // Call the parent method to process the payment
      onClose(); // Close modal after payment
    } else {
      alert('Harap lengkapi form pembayaran!');
    }
  };

  const totalHarga = show.cart.reduce((total, item) => total + item.harga * item.quantity, 0); // Calculate total price

  return (
    <div style={{ display: show ? 'block' : 'none', position: 'fixed', top: '0', left: '0', width: '100%', height: '100%', backgroundColor: 'rgba(0, 0, 0, 0.5)', zIndex: '1000' }}>
      <div style={{ position: 'relative', margin: 'auto', top: '50%', transform: 'translateY(-50%)', background: 'white', padding: '20px', width: '400px', borderRadius: '8px' }}>
        <h2>Form Pembayaran</h2>
        <p>Total Harga: Rp {totalHarga.toLocaleString()}</p>

        <div>
          <label>Metode Pembayaran:</label>
          <select value={metodePembayaran} onChange={(e) => setMetodePembayaran(e.target.value)}>
            <option value="">Pilih Metode Pembayaran</option>
            <option value="Transfer Bank">Transfer Bank</option>
            <option value="COD">COD</option>
          </select>
        </div>

        <div>
          <label>Nomor Rekening:</label>
          <input
            type="text"
            value={nomorRekening}
            onChange={(e) => setNomorRekening(e.target.value)}
            placeholder="Masukkan nomor rekening"
          />
        </div>

        <button onClick={handlePayment}>Bayar</button>
        <button onClick={onClose} style={{ marginTop: '10px' }}>Tutup</button>
      </div>
    </div>
  );
};

function Cart({ cart, onRemoveFromCart, onCheckout }) {
  const [showPaymentModal, setShowPaymentModal] = useState(false);

  const handleShowPaymentModal = () => {
    setShowPaymentModal(true);
  };

  const handleClosePaymentModal = () => {
    setShowPaymentModal(false);
  };

  const handlePayment = (metodePembayaran, nomorRekening) => {
    // You can implement API call to save payment details and update order status
    alert(`Pembayaran berhasil dengan metode ${metodePembayaran}. Nomor rekening: ${nomorRekening}`);
    // Call your API to update the payment status in the backend
    onCheckout();
  };

  const totalPrice = cart.reduce((total, item) => total + item.harga * item.quantity, 0);

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cart.length === 0 ? (
        <p>Keranjang belanja Anda kosong</p>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                {item.nama} - Rp {item.harga.toLocaleString()} per kg x {item.quantity} kg
                <button onClick={() => onRemoveFromCart(item)}>Hapus</button>
              </li>
            ))}
          </ul>
          <div>
            <strong>Total Harga: Rp {totalPrice.toLocaleString()}</strong>
          </div>
        </>
      )}

      <button onClick={handleShowPaymentModal} disabled={cart.length === 0}>
        {cart.length === 0 ? 'Keranjang Kosong' : 'Proses Pembayaran'}
      </button>

      <PaymentModal
        show={showPaymentModal}
        onClose={handleClosePaymentModal}
        onPayment={handlePayment}
        cart={cart} // Pass cart to PaymentModal to calculate total
      />
    </div>
  );
}

export default Cart;
