import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import OrderList from './components/OrderList';
import { login } from './api';  // Import API untuk login

function App() {
  const [role, setRole] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (username, password) => {
    try {
      const result = await login(username, password);
      if (result.role) {
        setRole(result.role);
        setUsername(username); // Set username
        setPassword(password); // Set password
      } else {
        setErrorMessage('Login gagal');
      }
    } catch (error) {
      setErrorMessage('Login gagal');
    }
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCart(cart.filter(item => item.id !== product.id));
  };

  const handleCheckout = () => {
    alert('Pembayaran selesai');
    // Handle checkout logic
  };

  return (
    <Router>
      <div className="App">
        {errorMessage && <div className="error-popup">{errorMessage}</div>}

        {!role ? (
          <Login onLogin={handleLogin} />
        ) : role === 'pembeli' ? (
          <Switch>
            <Route path="/home">
              <h1>Welcome, Pembeli</h1>
              <ProductList onAddToCart={handleAddToCart} />
              <Cart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />
            </Route>
          </Switch>
        ) : (
          <Switch>
            <Route path="/home">
              <h1>Welcome, Penjual</h1>
              <OrderList />
            </Route>
          </Switch>
        )}
      </div>
    </Router>
  );
}

export default App;
