import React, { useState, useEffect } from 'react';
import { createOrder } from '../api';

const PesananPage = () => {
  const userId = 1; // Contoh user ID
  const [metodePembayaran, setMetodePembayaran] = useState('');
  const [nomorRekening, setNomorRekening] = useState('');
  const [selectedItems, setSelectedItems] = useState([]);

  // Contoh pengaturan state jika data keranjang berasal dari server
  useEffect(() => {
    const cartItems = [
      { id_produk: 1, jumlah: 2 }, // Contoh item
    ];
    setSelectedItems(cartItems);
  }, []);

  const handlePesan = async () => {
    console.log({ userId, selectedItems, metodePembayaran, nomorRekening });

    if (!userId || !selectedItems.length || !metodePembayaran || !nomorRekening) {
      alert('Harap lengkapi semua data!');
      return;
    }

    try {
      const response = await createOrder(userId, selectedItems, metodePembayaran, nomorRekening);
      alert('Pesanan berhasil diproses!');
      console.log('Response dari server:', response);
    } catch (error) {
      console.error('Error saat memproses pesanan:', error);
      alert('Terjadi kesalahan: ' + error.message);
    }
  };

  return (
    <div>
      <h1>Halaman Pembayaran</h1>
      <input
        type="text"
        placeholder="Metode Pembayaran"
        value={metodePembayaran}
        onChange={(e) => setMetodePembayaran(e.target.value)}
      />
      <input
        type="text"
        placeholder="Nomor Rekening"
        value={nomorRekening}
        onChange={(e) => setNomorRekening(e.target.value)}
      />
      <button onClick={handlePesan}>Pesan</button>
    </div>
  );
};

export default PesananPage;
