import React, { useState, useEffect } from 'react';
import { fetchCart, removeFromCart } from '../api';  // Make sure these functions are imported

function Cart({ userId }) {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const loadCart = async () => {
      const data = await fetchCart(userId);
      setCartItems(data);
    };

    loadCart();
  }, [userId]);

  const handleRemoveFromCart = async (productId) => {
    const result = await removeFromCart(userId, productId);
    if (result.success) {
      setCartItems(cartItems.filter(item => item.product_id !== productId));
    } else {
      alert('Failed to remove item');
    }
  };

  return (
    <div>
      <h2>Keranjang Belanja</h2>
      {cartItems.length === 0 ? (
        <p>Keranjang Anda kosong</p>
      ) : (
        <ul>
          {cartItems.map(item => (
            <li key={item.product_id}>
              {item.product_name} - Rp {item.price} x {item.quantity}
              <button onClick={() => handleRemoveFromCart(item.product_id)}>Hapus</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Cart;
