import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { getProduk } from '../api'; // API Helper function untuk fetch data produk

// Styled Components
const Container = styled.div`
  padding: 20px;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const Th = styled.th`
  border: 1px solid #ddd;
  padding: 8px;
  background-color: #f2f2f2;
  text-align: left;
`;

const Td = styled.td`
  border: 1px solid #ddd;
  padding: 8px;
`;

const DataProduk = () => {
  const [produkList, setProdukList] = useState([]); // Menyimpan data produk dari database

  // Fetch data produk dari database saat komponen di-mount
  useEffect(() => {
    const fetchProduk = async () => {
      const data = await getProduk(); // Ambil data dari API
      setProdukList(data); // Set data ke state produkList
    };

    fetchProduk();
  }, []);

  return (
    <Container>
      <h2>Data Produk</h2>
      {/* Tabel Data Produk */}
      <Table>
        <thead>
          <tr>
            <Th>ID</Th>
            <Th>Nama Produk</Th>
            <Th>Harga</Th>
            <Th>Stok</Th>
          </tr>
        </thead>
        <tbody>
          {produkList.map((produk) => (
            <tr key={produk.id}>
              <Td>{produk.id}</Td>
              <Td>{produk.nama}</Td>
              <Td>{produk.harga}</Td>
              <Td>{produk.stok}</Td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default DataProduk;
